mapper.select:
SELECT USERNO, USERID, PASSWORD, USERNAME, GENDER, EMAIL, TO_CHAR(BIRTHDAY, 'YYYY/MM/DD') AS BIRTHDAY, PROFILEPIC, ENROLLDATE
FROM MEMBER
WHERE USERID = #{userId}

mapper.update:
UPDATE MEMBER
SET EMAIL = #{email}, BIRTHDAY = TO_DATE(#{birthday}, 'YYYY/MM/DD'), EMAIL = #{email}, PROFILEPIC = #{newProfilePic}
WHERE USERID = #{userId}
